}

.new-hero p {
    color: var(--text-secondary);
    margin-bottom: var(--space-4);
}

.new-buttons {
    display: flex;
    gap: var(--space-2);
    justify-content: center;
    flex-wrap: wrap;
}

.new-button-primary {
    background: var(--gradient-primary);
    color: white;
    border: none;
    padding: var(--space-2) var(--space-3);
